mapper.insert:
insert into place_lease_record (place_id, start_time,
end_time, user_id, cost
)
values (#{placeId,jdbcType=INTEGER}, #{startTime,jdbcType=TIMESTAMP},
#{endTime,jdbcType=TIMESTAMP}, #{userId,jdbcType=INTEGER},
#{cost,jdbcType=INTEGER}
)

mapper.select:
select count(0) from place_lease_record left join place p on place_id = p.id
place_name like '%' #{keyWords} '%'
place_name = #{keyWords}

select plr.id, plr.place_id, plr.start_time, plr.end_time, plr.user_id, plr.cost, plr.result, plr.pay_status, plr.check_status, p.place_name, u.account from place_lease_record plr left join place p on plr.place_id = p.id left join gym_user u on plr.user_id = u.id
p.place_name like '%' #{keyWords} '%'
p.place_name = #{keyWords}
limit #{startColum},#{pageSize}

mapper.update:
update place_lease_record
set
result = #{result,jdbcType=VARCHAR},
check_status = #{checkStatus,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
